import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios"

const Create = (props) => {
    //Declare State
    const [name, setName] = useState("")
    const [url, setUrl] = useState("")
    const [chests, setChests] = useState(0)
    const [catchPhrase, setCatchPhrase] = useState("")
    const [position, setPosition] = useState("")
    const [pegLeg, setPegLeg] = useState(false)
    const [eyePatch, setEyePatch] = useState(false)
    const [hookHand, setHookHand] = useState(false)

    //For navigate
    const navigate = useNavigate();


    //Handler Functions
    //Create an array to store errors from the API
    const [errors, setErrors] = useState([]); 
    const submitHandler = e => {
        e.preventDefault();
        // Create obj with pirate info
        const pirateObj = {
            name,
            url,
            chests,
            catchPhrase,
            position,
            pegLeg,
            eyePatch,
            hookHand
        }
        //Make post request to express with state
        axios.post("http://localhost:8000/api/pirates/new", {pirateObj})
            .then(res => console.log(res))
            .catch(err=>{
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr);
            })
        }


    return (
    <fieldset>
        <legend>Create.jsx</legend>
        <div id='header'>
            <h1>Add Pirate <button>Crew Board</button></h1>
        </div>
        <form onSubmit={submitHandler}>
            <p>
                Name:
                <input type="text" name="name" onChange={(e) => setName(e.target.value)}/>
            </p>
            <p>
                Image Url:
                <input type="text" name="url" onChange={(e) => setUrl(e.target.value)}/>
            </p>
            <p>
                # of Treasure Chests
                <input type="number" name='chests' onChange={(e) => setChests(e.target.value)}/>
            </p>
            <p>
                Catch Phrase:
                <input type="text" name="catchPhrase" onChange={(e) => setCatchPhrase(e.target.value)}/>
            </p>
            <p>
                Crew Position:
                <select name="position" id="">
                <option value="First Mate">First Mate</option>
                <option value="Quarter Master">Quarter Master</option>
                <option value="Boatswain">Boatswain</option>
                <option value="Powder Monkey">Powder Monkey</option>
            </select>
            </p>
            <p>
                <input type="checkbox" name='pegLeg' />
                Peg Leg
            </p>
            <p>
                <input type="checkbox" name='eyePatch' />
                Eye Patch
            </p>
            <p>
                <input type="checkbox" name='handHook'/>
                Hand Hook
            </p>
            <button>Add Pirate</button>
        </form> 
    </fieldset>
    )
}

export default Create
