import React, {useEffect, useState} from 'react'
import axios from "axios"
import { Link } from 'react-router-dom';

const Display = (props) => {
    //state to hold all coming from db
    const [allPirates, setAllPirates] = useState([])

    //state to track refresh
    const [refreshState, setRefreshState] = useState(false)

    const refresh = () => {
        setRefreshState(!refreshState)
    }

    useEffect(() => {
        //make a call to express to get all
        axios.get("http://localhost:8000/api/pirates")
        .then(res => setAllPirates(res.data)) //incoming info is set to state
        .catch(err => console.log(err))
    }, [refreshState])


    return (
    <fieldset>
        <legend>Display.jsx</legend>
        <table>
            <tbody>
            { allPirates.map((pirate, index) => {
                return (
                    <tr key={index}>
                        <td>{pirate.url}</td>
                        <td>{pirate.name}</td>
                        <td>
                            {/* <Link to={`/edit/${author._id}`}>Edit</Link>
                            <button onClick={(e) => deleteHandler(author._id)}>Delete</button> */}
                        </td>
                    </tr>
                )
            })}
            </tbody>
        </table>
    </fieldset>
    )
}

export default Display
